const bodyRequest = {
  "article": {
    "tagList": [],
    "title": "Request from API",
    "description": "API Testing is easy",
    "body": "Angular is cool"
  }
}; //getting the token from the alias saved in the login method
cy.get('@token').then(token => {
  //sending the POST request to create the article passing header and parameters
  cy.request({
    url: Cypress.env('apiUrl') + 'api/articles/',
    headers: {
      'Authorization': 'Token ' + token
    },
    method: 'POST',
    body: bodyRequest
  }).then(response => {
    expect(response.status).to.equal(200); // checking that the POST was successfull
  }); // going to the recently created article(the first one on the list) to delete it. 
  cy.contains('Global Feed').click();
  cy.get('.article-preview').first().click();
  cy.get('.article-actions').contains('Delete Article').click(); //getting the list of articles to make sure the deleted one is not present.
  cy.request({
    url: Cypress.env('apiUrl') + 'api/articles?limit=10&offset=0',
    headers: {
      'Authorization': 'Token ' + token
    },
    method: 'GET'
  }).its('body').then(body => {
    expect(body.articles[0].title).not.equal('Request from API');
  });
});
